function dragstart_handler(e){e.dataTransfer.dropEffect="copy",e.dataTransfer.setData("text/plain",e.target.innerText)}function dragover_handler(e){e.preventDefault(),e.dataTransfer.dropEffect="move",e.target.style.background="coral"}function dragleave_handler(e){e.target.style.background="lightblue"}function drop_handler(e){e.preventDefault(),console.log(e);var t=e.dataTransfer.getData("text/plain");console.log(t),e.target.appendChild(document.getElementById(t))}